private void B_AddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_B_AddActionPerformed
    String sql;
    Connection con = DBC.ConnectDB();
    
    
    sql="Insert into members values(" + T_ID.getText();
    sql=sql + ",'" + T_Name.getText() + "','" + T_Sex.getText() + "'," + T_Age.getText() + ",'" ;
    sql=sql+ T_Address.getText() + "',0,'"+new String(T_Pass.getPassword())+"')";
    DBC.ExecuteSQL(con, sql);
        
    DBC.DisconnectDB(con);
        
    this.dispose();
}//GEN-LAST:event_B_AddActionPerformed

