private void B_UpdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_B_UpdActionPerformed
    String sql;
    Connection con = DBC.ConnectDB();
        
    sql="update members set name='" + T_Name.getText()+"' where id=" + T_ID.getText();
    DBC.ExecuteSQL(con, sql);
    sql="update members set sex='" + T_Sex.getText()+"' where id=" + T_ID.getText();
    DBC.ExecuteSQL(con, sql);
    sql="update members set age=" + T_Age.getText()+" where id=" + T_ID.getText();
    DBC.ExecuteSQL(con, sql);
    sql="update members set address='" + T_Address.getText() + "' where id=" + T_ID.getText();
    DBC.ExecuteSQL(con, sql);
    sql="update members set password='" + new String(T_Pass.getPassword()) + "' where id=" + T_ID.getText();
    DBC.ExecuteSQL(con, sql);
    
    DBC.DisconnectDB(con);
        
    this.dispose();
}//GEN-LAST:event_B_UpdActionPerformed

