public void UpdateFields(int id)
{
    Connection con = DBC.ConnectDB();
    ResultSet R = DBC.getTable(con, "select * from members where id=" + id);
        try {
            R.first();
            T_ID.setText(R.getInt("id") + "");
            T_Name.setText(R.getString("name"));
            T_Sex.setText(R.getString("sex"));
            T_Age.setText(R.getInt("age")+"");
            T_Address.setText(R.getString("address"));
            T_Pass.setText(R.getString("password"));
            //Availability can't be changed from here.
        } catch (Exception e) {
            System.out.println(e);
        }
    
    DBC.DisconnectDB(con);
    this.setVisible(true);
}
