private void B_UpdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_B_UpdActionPerformed
    String sql;
    Connection con = DBC.ConnectDB();
    sql="update suppliers set name='" + T_Name.getText()+"' where id=" + T_ID.getText();
    DBC.ExecuteSQL(con, sql);
    sql="update suppliers set mobile='" + T_Mobile.getText()+"' where id=" + T_ID.getText();
    DBC.ExecuteSQL(con, sql);
    sql="update suppliers set address='" + T_Address.getText() + "' where id=" + T_ID.getText();
    DBC.ExecuteSQL(con, sql);    
    DBC.DisconnectDB(con);
    this.dispose();
}//GEN-LAST:event_B_UpdActionPerformed

