public void UpdateFields(int id)
{
    Connection con = DBC.ConnectDB();
    ResultSet R = DBC.getTable(con, "select * from suppliers where id=" + id);
        try {
            R.first();
            T_ID.setText(R.getInt("id") + "");
            T_Name.setText(R.getString("name"));
            T_Mobile.setText(R.getString("mobile"));
            T_Address.setText(R.getString("address"));
        } catch (Exception e) {System.out.println(e);}
        DBC.DisconnectDB(con);
        this.setVisible(true);
}

